    /**
     * Prepares the code to insert the third party video
     *
     * @param string $row  the $video_id output containing video paramters
     * @param int    $flv_width  the video width
     * @param int    $flv_height  the video height
     * @return       $code   the third party embed code
     */
    function ustreamTvPrepareVideo($row, $flv_width=null, $flv_height=null)
	{
		global $show_video_ad, $pre_url, $post_url, $smartyvs, $task;
		if (!defined('HWDVIDSPATH')) { define('HWDVIDSPATH', dirname(__FILE__).'/../../'); }
		$c = hwd_vs_Config::get_instance();

			$code=null;

			if (@explode(",", $row->video_id)) {
				$data = explode(",", $row->video_id);
				$videocode = $data[0];
			} else {
			    return $code;
			}

			if ($c->playlocal == "1") {
				$truepath = ustreamTvPrepareFlvURL($row->video_id);
			} else {
				$truepath = '';
			}

			$thumb_url = (!empty($row->thumbnail) ? $row->thumbnail : $data[1]);

			if (!empty($truepath) && $c->playlocal == "1") {

			} else {

				if ($flv_width==null) {
					$smartyvs->assign("player_width", $c->flvplay_width);
					$flv_width = $c->flvplay_width;
				} else {
					$smartyvs->assign("player_width", $flv_width);
					$flv_width = $flv_width;
				}
				if ($flv_height==null) {
					$flv_height = intval ($flv_width*$c->var_fb);
					$flv_height = $flv_height+25;
				} else {
					$flv_height = $flv_height;
				}

				$code = '<object classid="clsid:d27cdb6e-ae6d-11cf-96b8-444553540000" width="'.$flv_width.'" height="'.$flv_height.'" id="utv766284" name="utv_n_794785"><param name="flashvars" value="loc=%2F&amp;autoplay=false&amp;vid='.$videocode.'" /><param name="allowfullscreen" value="true" /><param name="allowscriptaccess" value="always" /><param name="src" value="http://www.ustream.tv/flash/video/'.$videocode.'" /><embed flashvars="loc=%2F&amp;autoplay=false&amp;vid='.$videocode.'" width="'.$flv_width.'" height="'.$flv_height.'" allowfullscreen="true" allowscriptaccess="always" id="utv766284" name="utv_n_794785" src="http://www.ustream.tv/flash/video/'.$videocode.'" type="application/x-shockwave-flash" /></object>';
				return $code;
			}
	}
    /**
     * Prepares the code to insert the third party thumbnail image
     *
     * @param string $option  the $video_id output containing video paramters
     * @param int    $vid  the video database id
     * @param int    $Itemid  the joomla menu id
     * @param int    $k  the alternating CSS integer
     * @return       $code   the full third party thumbnail image tag
     */
	function ustreamTvPrepareThumb($option, $vid, $Itemid, $k, $width=null, $height=null, $class=null, $tooltip_data=null)
	{
		if (!isset($width)) {
			$width = $c->thumbwidth;
		}
		if (!isset($height)) {
			$height = $width*$c->tar_fb;
		}

		$code = "<img src=\"".ustreamTvPrepareThumbURL($option, $vid)."\" border=\"0\" width=\"".$width."\" height=\"".$height."\" title=\"".$tooltip_data[1]." :: ".$tooltip_data[2]."\" class=\"".$class."\" />";
		return $code;
	}
    /**
     * Prepares the code to insert the third party thumbnail image
     *
     * @param string $option  the $video_id output containing video paramters
     * @param int    $vid  the video database id
     * @param int    $Itemid  the joomla menu id
     * @param int    $k  the alternating CSS integer
     * @return       $code   the full third party thumbnail image tag
     */
	function ustreamTvPrepareThumbURL($option, $vid)
	{
		$data = explode(",", $option);
		$thumbnail = @$data[1];

		if (!$thumbnail) {
			$code = URL_HWDVS_IMAGES.'default_thumb.jpg';
		} else {
			$code = $thumbnail;
		}
		return $code;
	}
    /**
     * Prepares the third party video link
     *
     * @param string $option  the $video_id output containing video paramters
     * @param int    $vid  the video database id
     * @param int    $Itemid  the joomla menu id
     * @return       $code   the full third party thumbnail image tag
     */
	function ustreamTvPrepareVideoURL($option)
	{
			if (@explode(",", $option)) {
				$data = explode(",", $option);
				$videocode = $data[0];
			} else { $videocode = "ERROR"; }

			$code = "http://www.ustream.tv/recorded/".$videocode;
			return $code;
	}
    /**
